package ch.specchio.gui;import ch.specchio.client.SPECCHIOClient;import javax.swing.*;import javax.swing.event.ChangeEvent;import javax.swing.event.ChangeListener;import javax.swing.event.TreeSelectionEvent;import javax.swing.event.TreeSelectionListener;import java.awt.*;import java.awt.dnd.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.awt.event.KeyEvent;//import ptolemy.plot.Plot;public class InfoVisMainFrame extends JFrame {    private TextPanel textPanel;    private Toolbar toolbar;    private DataPanel dataPanel;    private DataSelectionPanel dataSelectionPanel;//    private Plot testPlot;    public InfoVisMainFrame(){        super("InfoVis Toolbox");        SPECCHIOClient specchioClient = SPECCHIOApplication.getInstance().getClient();        setLayout(new BorderLayout());        textPanel = new TextPanel();        toolbar = new Toolbar();        dataPanel = new DataPanel();        dataSelectionPanel = new DataSelectionPanel();//        testPlot = new Plot();        // SETUP PLOTS//        testPlot.setSize(100, 100);//        testPlot.setButtons(true);//        testPlot.setTitle("Spectral Plot");//        testPlot.setMarksStyle("none");        // SETUP HIERARCHY BROWSER:        // load all campaigns        setJMenuBar(createMenuBar());        toolbar.setStringListener(new StringListener() {            @Override            public void textEmitted(String text) {                textPanel.appendTextArea(text);            }        });        dataSelectionPanel.getDataPanel().setDataVisListener(new DataVisListener(){            public void dataVisEventOccurred(DataVisEvent e) {                String name = e.getName();                String occupation = e.getOccupation();                int ageCat = e.getAgeCategory();                String empCat = e.getEmpCat();                if(e.isUsCitizen()) {                    String taxId = e.getTaxId();                    textPanel.appendTextArea("Name = " + name + ", Occupation = " + occupation + ", Age Category = " + ageCat + ", Employment = " + empCat +                            ", US TAX ID = " + taxId + "\n");                } else {                    textPanel.appendTextArea("Name = " + name + ", Occupation = " + occupation + ", Age Category = " + ageCat + ", Employment = " + empCat + "\n");                }                textPanel.appendTextArea(e.getGender() + "\n");            }        });//        add(dataPanel, BorderLayout.WEST);        add(dataSelectionPanel, BorderLayout.WEST);        add(textPanel, BorderLayout.CENTER);        add(toolbar, BorderLayout.NORTH);//        add(testPlot, BorderLayout.CENTER);        setDefaultCloseOperation(JFrame.DISPOSE_ON_CLOSE);        setMinimumSize(new Dimension(500, 500));        setSize(1000,1000);        setVisible(true);    }    private JMenuBar createMenuBar(){        JMenuBar menuBar = new JMenuBar();        JMenu fileMenu = new JMenu("File");        JMenuItem exportDataItem = new JMenuItem("Export Data...");        JMenuItem importDataItem = new JMenuItem("Import Data...");        JMenuItem exitItem = new JMenuItem("Exit");        fileMenu.add(exportDataItem);        fileMenu.add(importDataItem);        fileMenu.addSeparator();        fileMenu.add(exitItem);        JMenu windowMenu = new JMenu("Window");        JMenu showMenu = new JMenu("Show");        JCheckBoxMenuItem showFormItem = new JCheckBoxMenuItem("Spectral Data Browser");        showFormItem.setSelected(true);        windowMenu.add(showMenu);        showMenu.add(showFormItem);        menuBar.add(fileMenu);        menuBar.add(windowMenu);        fileMenu.setMnemonic(KeyEvent.VK_F);        exitItem.setMnemonic(KeyEvent.VK_X);        exitItem.setAccelerator(KeyStroke.getKeyStroke(KeyEvent.VK_X, ActionEvent.CTRL_MASK));        exitItem.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                int action = JOptionPane.showConfirmDialog(InfoVisMainFrame.this, "Quit Toolbox?", "Confirm Exit", JOptionPane.OK_CANCEL_OPTION);                if(action == JOptionPane.OK_OPTION) {                    setVisible(false);                }            }        });        JMenu selectioMenu = new JMenu("Selection");        menuBar.add(selectioMenu);        JMenu toolsMenu = new JMenu("Tools");        menuBar.add(toolsMenu);        return menuBar;    }}